package basics;

public class ForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=25;i++) {
			System.out.print(i+" ");
		}
		System.out.println("");
		System.out.println("********** Display 55 -20 **********");
	       
        for(j=55;j>=20;j--)
        {
            System.out.print(j+" ");
        }
	}

}
